from	repasse_terceiro_venc a
where	a.nr_repasse_terceiro	= nr_repasse_new_p;

if (cont_venc_w > 0) then
	if (nr_repasse_new_p IS NOT NULL AND nr_repasse_new_p::text <> '') and
		((vl_repasse_old_p <> vl_repasse_new_p) or (vl_liberado_old_p <> vl_liberado_old_p)) then
