	end if;

	if (coalesce(nr_repasse_new_p::text, '') = '') and (nr_repasse_old_p IS NOT NULL AND nr_repasse_old_p::text <> '') then
		-- Este repasse já possui vencimentos gerados! Não é possível desvincular o repasse.
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266927);
	end if;

	if (nr_repasse_new_p IS NOT NULL AND nr_repasse_new_p::text <> '') and (coalesce(nr_repasse_old_p::text, '') = '') then
