CREATE OR REPLACE PROCEDURE consistir_via_acesso_pac_atend (nr_seq_atendimento_p bigint, ds_itens_inconsistentes_p INOUT text) AS $body$
DECLARE


ds_material_w		varchar(255);
ie_via_aplicacao_w	varchar(20);
ds_erro_w		varchar(2000) := '';
cont_w			bigint;

c01 CURSOR FOR
SELECT	substr(obter_desc_material(CD_MATERIAL),1,60),
	a.ie_via_aplicacao
from	paciente_atend_medic a
where	nr_seq_atendimento	= nr_seq_atendimento_p
and	coalesce(nr_seq_diluicao::text, '') = ''
and	coalesce(nr_seq_via_acesso::text, '') = ''
and	(ie_via_aplicacao IS NOT NULL AND ie_via_aplicacao::text <> '');


BEGIN

open c01;
loop
fetch c01 into
	ds_material_w,
	ie_via_aplicacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	select	coalesce(count(*),0)
	into STRICT	cont_w
	from	VIA_APLICACAO_ACESSO
	where	IE_VIA_APLICACAO = ie_via_aplicacao_w;

	if (coalesce(cont_w,0) > 0) then
		ds_erro_w	:=  substr(ds_material_w ||  chr(13)  || ds_erro_w,1,2000);
	end if;
end loop;
close c01;

ds_itens_inconsistentes_p	:= ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
