SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consistir_vinculo_nf_tit_pag ( nr_sequencia_p bigint, ds_lista_titulos_p text, ie_vincular_p text, nm_usuario_p text, ds_consistencia_p INOUT text) AS $body$
DECLARE


ds_lista_titulos_w			varchar(2000);
nr_titulo_w			bigint;
vl_total_titulo_w			double precision;
vl_titulo_w			double precision;
vl_total_nota_w			double precision;
ds_mensagem_w			varchar(500);
tam_lista_w			bigint;
ie_pos_virgula_w			bigint;
ie_vincular_w			varchar(1);
cd_estabelecimento_w		integer;
ie_consistir_total_w		varchar(1);

c01 CURSOR FOR
SELECT	nr_titulo
from	titulo_receber
where	nr_seq_nf_saida = nr_sequencia_p;


BEGIN

select	vl_total_nota,
	cd_estabelecimento
into STRICT	vl_total_nota_w,
	cd_estabelecimento_w
from	nota_fiscal
where	nr_sequencia = nr_sequencia_p;

ie_consistir_total_w := obter_param_usuario(40, 368, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_consistir_total_w);

ie_vincular_w	:= ie_vincular_p;
ds_lista_titulos_w	:= ds_lista_titulos_p;

if (ie_consistir_total_w = 'S') then
	begin

	open c01;
	loop
	fetch c01 into
		nr_titulo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		ds_lista_titulos_w	:= (ds_lista_titulos_w || to_char(nr_titulo_w) || ',');

		end;
	end loop;
	close c01;

	nr_titulo_w	:= 0;
	vl_total_titulo_w	:= 0;
	vl_titulo_w	:= 0;
	ds_mensagem_w	:= '';

	while(ds_lista_titulos_w IS NOT NULL AND ds_lista_titulos_w::text <> '') loop
		begin

		tam_lista_w		:=	length(ds_lista_titulos_w);
		ie_pos_virgula_w		:=	position(',' in ds_lista_titulos_w);

		if (ie_pos_virgula_w <> 0) then
			nr_titulo_w	:= (substr(ds_lista_titulos_w,1,(ie_pos_virgula_w - 1)))::numeric;
			ds_lista_titulos_w	:= substr(ds_lista_titulos_w,(ie_pos_virgula_w + 1),tam_lista_w);
		end if;

		select	coalesce(max(vl_titulo),0)
		into STRICT	vl_titulo_w
		from	titulo_receber
		where	nr_titulo = nr_titulo_w;

		vl_total_titulo_w	:= vl_total_titulo_w + vl_titulo_w;

		end;
	end loop;

	if (vl_total_titulo_w > vl_total_nota_w) then
		begin
		ie_vincular_w := 'N';
		ds_mensagem_w := wheb_mensagem_pck.get_texto(313842,'VL_TOTAL_NOTA='||campo_mascara_virgula(vl_total_nota_w)||';VL_TOTAL_TITULO='||campo_mascara_virgula(vl_total_titulo_w));
		end;
	end if;

	end;
end if;

if (ie_vincular_w = 'S') then
	begin

	ds_lista_titulos_w	:= ds_lista_titulos_p;
	nr_titulo_w	:= 0;
	vl_total_titulo_w	:= 0;
	vl_titulo_w	:= 0;

	while(ds_lista_titulos_w IS NOT NULL AND ds_lista_titulos_w::text <> '') loop
		begin

		tam_lista_w		:=	length(ds_lista_titulos_w);
		ie_pos_virgula_w		:=	position(',' in ds_lista_titulos_w);

		if (ie_pos_virgula_w <> 0) then
			nr_titulo_w	:= (substr(ds_lista_titulos_w,1,(ie_pos_virgula_w - 1)))::numeric;
			ds_lista_titulos_w	:= substr(ds_lista_titulos_w,(ie_pos_virgula_w + 1),tam_lista_w);
		end if;

		CALL vincular_titulo_receber_nf(nr_sequencia_p,nr_titulo_w,nm_usuario_p);

		end;
	end loop;

	end;
end if;

ds_consistencia_p	:= ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consistir_vinculo_nf_tit_pag ( nr_sequencia_p bigint, ds_lista_titulos_p text, ie_vincular_p text, nm_usuario_p text, ds_consistencia_p INOUT text) FROM PUBLIC;

