CREATE OR REPLACE PROCEDURE consistir_vl_conta_vl_nf (nr_interno_conta_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, vl_total_nf_p INOUT bigint) AS $body$
DECLARE

 
c001			integer;
nr_seq_protocolo_w	bigint;
nr_interno_conta_w	bigint;
cd_convenio_w		bigint;
ds_sql_w		varchar(2000);
vl_total_nf_w		double precision:=0;
vl_proc_mat_w		double precision;
vl_conta_w		double precision;

C01 CURSOR FOR 
	SELECT	UPPER(ds_sql) 
	FROM	parametro_nfs_lista 
	WHERE	cd_estabelecimento = cd_estabelecimento_p 
	AND	cd_convenio	  = cd_convenio_w;


BEGIN 
 
SELECT	nr_seq_protocolo, 
    nr_interno_conta, 
	cd_convenio_parametro 
INTO STRICT	nr_seq_protocolo_w, 
	nr_interno_conta_w, 
	cd_convenio_w 
FROM	conta_paciente 
WHERE	nr_interno_conta = nr_interno_conta_p;
 
vl_conta_w:= coalesce(obter_valor_conta(nr_interno_conta_w,0),0);
 
 
 
OPEN C01;
LOOP 
FETCH C01 INTO 
	ds_sql_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	BEGIN 
 
 
 
	ds_sql_w := REPLACE(ds_sql_w, ':NR_INTERNO_CONTA', coalesce(nr_interno_conta_w,0));
	ds_sql_w := REPLACE(ds_sql_w, ':NR_SEQ_PROTOCOLO', coalesce(nr_seq_protocolo_w,0));
	ds_sql_w := REPLACE(ds_sql_w, ':NR_SEQ_LOTE_PROTOCOLO',0);
 
	vl_proc_mat_w := obter_valor_Dinamico(ds_sql_w, vl_proc_mat_w);
 
	vl_total_nf_w := vl_total_nf_w + vl_proc_mat_w;
 
 
 
	END;
END LOOP;
CLOSE C01;
 
vl_total_nf_p := vl_total_nf_w;
 
 
COMMIT;
 
END;
$body$
