CREATE OR REPLACE PROCEDURE consist_cancela_agenda_ageserv ( ie_cancel_age_fut_p text, cd_agenda_p bigint, dt_agenda_p timestamp, cd_pessoa_fisica_p text, cd_motivo_p text, ds_observacao_p text, nr_seq_agenda_p bigint, ie_cancel_lista_espera_p text, nr_seq_lista_espera_p bigint, nm_usuario_p text, ds_ret_regra_alt_status_p INOUT text, cd_profissional_p text default null) AS $body$
