CREATE OR REPLACE PROCEDURE consist_variacao_nf_emprestimo ( nr_sequencia_p bigint, nm_usuario_p text, ds_lista_itens_p INOUT text) AS $body$
DECLARE

 
cd_material_w			integer;
qt_registros_w			bigint;
ds_lista_itens_w			varchar(255);
cd_estabelecimento_w		bigint;
cd_setor_atendimento_w		bigint;

c01 CURSOR FOR 
SELECT	distinct cd_material 
from	nota_fiscal_item 
where	nr_sequencia = nr_sequencia_p 
and	(nr_emprestimo IS NOT NULL AND nr_emprestimo::text <> '');


BEGIN 
ds_lista_itens_w := '';
select	cd_estabelecimento, 
	cd_setor_digitacao 
into STRICT	cd_estabelecimento_w, 
	cd_setor_atendimento_w 
from	nota_fiscal 
where	nr_sequencia = nr_sequencia_p;
 
open C01;
loop 
fetch C01 into	 
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	select	fis_consistir_variacao_fiscal(cd_material_w,nr_Sequencia_p) 
	into STRICT	qt_registros_w 
	;
 
	if (qt_registros_w <= 0) then 
		CALL gravar_log_fiscal(cd_material_w, nr_Sequencia_p, null, 1, nm_usuario_p);
		fis_enviar_comunicao_interna(cd_setor_atendimento_w, cd_estabelecimento_w, nm_usuario_p, nr_Sequencia_p, cd_material_w);
		ds_lista_itens_w := substr(ds_lista_itens_w || cd_material_w || ' ,',1,255);
	end if;		
	end;
end loop;
close C01;
 
commit;
 
ds_lista_itens_p := substr(ds_lista_itens_w,1,length(ds_lista_itens_w) -2);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
