CREATE OR REPLACE PROCEDURE consis_atrib_cad_compl_pf_eup (cd_pessoa_fisica_p text, nr_seq_regra_compl_p bigint, ds_mensagem_p INOUT text, ie_obriga_p INOUT text) AS $body$
DECLARE


--Globais
cd_setor_atendimento_w		bigint;
cd_estabelecimento_w		bigint;
cd_perfil_w			bigint;
nm_usuario_w			varchar(20);

ie_atrib_obrig_w		varchar(2);
ds_regra_w			varchar(4000);
ds_mensagem_w			varchar(255);



BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	cd_setor_atendimento_w	:= wheb_usuario_pck.get_cd_setor_atendimento;
	cd_estabelecimento_w	:= obter_estabelecimento_ativo;
	cd_perfil_w		:= obter_perfil_ativo;
	nm_usuario_w		:= obter_usuario_ativo;

	SELECT * FROM consistir_regra_compl_pf(cd_estabelecimento_w, cd_perfil_w, cd_pessoa_fisica_p, cd_setor_atendimento_w, ie_obriga_p, ds_regra_w, ie_atrib_obrig_w, 'U', nr_seq_regra_compl_p) INTO STRICT ie_obriga_p, ds_regra_w, ie_atrib_obrig_w;
	if (ie_obriga_p <> 'S') then
		ie_obriga_p := coalesce(ie_atrib_obrig_w,'N');
		ds_mensagem_p := '';
	else
		ds_mensagem_p := substr(obter_texto_tasy(186881, wheb_usuario_pck.get_nr_seq_idioma),1,255);
	end if;



end if;

end;
