CREATE OR REPLACE PROCEDURE consis_cancela_agenda_paciente ( nr_seq_agenda_p bigint, cd_estabelecimento_p bigint, cd_motivo_p text, nm_usuario_p text, ds_observacao_p text) AS $body$
DECLARE

ds_valor_parametro_w varchar(255);

BEGIN
if (cd_motivo_p IS NOT NULL AND cd_motivo_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then
	begin
	/*Cancelando a agenda selecionada*/

	CALL cancelar_agenda_paciente(
		nr_seq_agenda_p,
		0,
		nm_usuario_p,
		cd_motivo_p,
		ds_observacao_p,
		'S');
	/*Verificando o envio de comunicação*/

	ds_valor_parametro_w := obter_parametro_funcao(871, 163, nm_usuario_p);
	if (ds_valor_parametro_w = 'D') then
		begin
		CALL verifica_envio_comunic_opme(
			'C',
			cd_estabelecimento_p,
			nr_seq_agenda_p,
			nm_usuario_p,
			null);
		end;
	end if;
	/*Cancelar, se a agenda possuir, o vículo na gestão de vagas*/

	ds_valor_parametro_w := obter_parametro_funcao(871, 374, nm_usuario_p);
	if (ds_valor_parametro_w = 'S') then
		begin
		CALL cancelar_gestao_vaga(
			nr_seq_agenda_p,
			cd_motivo_p,
			nm_usuario_p,
			cd_estabelecimento_p);
		end;
	end if;
	end;
end if;
end;
