CREATE OR REPLACE PROCEDURE consis_estrut_regra_ccusto_js ( cd_grupo_material_p bigint, cd_subgrupo_material_p bigint, cd_classe_material_p bigint, cd_material_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


qt_existe_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	estrutura_material_v
where	((cd_material_p = 0) or
	(cd_material_p > 0 AND cd_material = cd_material_p))
and	((cd_classe_material_p = 0) or
	(cd_classe_material_p > 0 AND cd_classe_material = cd_classe_material_p))
and	((cd_subgrupo_material_p = 0) or
	(cd_subgrupo_material_p > 0 AND cd_subgrupo_material = cd_subgrupo_material_p))
and	((cd_grupo_material_p = 0) or
	(cd_grupo_material_p > 0 AND cd_grupo_material = cd_grupo_material_p));

if (qt_existe_w = 0) then
	ds_erro_p := obter_texto_tasy(93095, wheb_usuario_pck.get_nr_seq_idioma);
end if;

commit;

end;
$body$
