CREATE OR REPLACE PROCEDURE consit_gerar_grupo_item_pepo ( nr_cirurgia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_pepo_grupo_item_grafico_w	bigint;
ie_grupo_item_pepo_w		varchar(15);

c01 CURSOR FOR
	SELECT	a.vl_dominio ie_grupo_item_pepo
	FROM valor_dominio a
LEFT OUTER JOIN pepo_grupo_item_grafico b ON (a.vl_dominio = b.ie_grupo_item_pepo AND nm_usuario_p = b.nm_usuario)
WHERE a.cd_dominio = 2864;


BEGIN

select	count(*)
into STRICT	qt_pepo_grupo_item_grafico_w
from	pepo_grupo_item_grafico
where	nm_usuario = nm_usuario_p;

if (coalesce(qt_pepo_grupo_item_grafico_w, 0) = 0) then
	begin
	open c01;
	loop
	fetch c01 into ie_grupo_item_pepo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		CALL gerar_grupo_item_pepo(nm_usuario_p, nr_cirurgia_p, ie_grupo_item_pepo_w);
		end;
	end loop;
	close c01;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
