CREATE OR REPLACE PROCEDURE consulta_gera_atend_quimio ( nr_seq_paciente_p bigint, nr_seq_atendimento_p bigint, nr_atendimento_p bigint, ds_estagio_autor_p text, dt_prevista_p timestamp, ie_opcao_p text, nm_usuario_p text, cd_estabelecimento_p bigint, cd_pessoa_fisica_p INOUT text, cd_medico_resp_p INOUT text, ds_erro_p INOUT text) AS $body$
DECLARE

 
/*	ie_opcao_p 
	C - Consulta 
	G - Gera	*/
 
 
ie_permite_atend_autorizado_w	varchar(1);
ie_permite_vincular_atend_w	varchar(1);


BEGIN 
 
if (ie_opcao_p = 'C') then 
	begin 
	/* Quimioterapia - Parâmetro [99] - Permitir gerar atendimento apenas para pacientes onde o Estágio autorização esteja como Autorizado */
 
	ie_permite_atend_autorizado_w := obter_param_usuario(3130, 99, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_permite_atend_autorizado_w);
 
	if (ie_permite_atend_autorizado_w = 'S') then 
		begin 
		select	CASE WHEN ds_estagio_autor_p=substr(obter_texto_tasy(60067, wheb_usuario_pck.get_nr_seq_idioma),1,255) THEN  				substr(obter_texto_tasy(60068, wheb_usuario_pck.get_nr_seq_idioma),1,255)  ELSE '' END  
		into STRICT	ds_erro_p 
		;
		end;
	end if;
 
	if (coalesce(nr_atendimento_p::text, '') = '') then 
		begin 
		select	max(cd_pessoa_fisica), 
			max(cd_medico_resp) 
		into STRICT	cd_pessoa_fisica_p, 
			cd_medico_resp_p 
		from	paciente_setor 
		where	nr_seq_paciente = nr_seq_paciente_p;
		end;
	end if;
	end;
 
elsif (ie_opcao_p = 'G') then 
	begin 
	/* Quimioterapia - Parâmetro [72] - Vincula os agendamentos diários do paciente ao gerar novo atendimento. */
 
	ie_permite_vincular_atend_w := obter_param_usuario(3130, 72, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_permite_vincular_atend_w);
 
	if (ie_permite_vincular_atend_w = 'S') then 
		begin 
		update	paciente_atendimento 
		set	dt_chegada	= clock_timestamp(), 
			nm_usuario	= nm_usuario_p, 
			nr_atendimento	= nr_atendimento_p 
		where	obter_dados_paciente_setor(nr_seq_paciente, 'C') = obter_dados_paciente_setor(nr_seq_paciente_p, 'C') 
		and (coalesce(nr_atendimento::text, '') = '' or nr_atendimento = 0) 
		and	dt_prevista between trunc(dt_prevista_p) and fim_dia(dt_prevista_p);
		end;
	else 
		begin 
		update	paciente_atendimento 
		set	dt_chegada	= clock_timestamp(), 
			nm_usuario	= nm_usuario_p, 
			nr_atendimento	= nr_atendimento_p 
		where	nr_seq_atendimento	= nr_seq_atendimento_p;
		end;
	end if;
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
