CREATE OR REPLACE PROCEDURE cons_atualiza_prescr_atend_pac ( nr_seq_paciente_p bigint, nr_atendimento_p bigint, nr_prescricao_p bigint, nr_seq_atendimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
ie_pessoa_w	varchar(1);


BEGIN

select	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	paciente_setor
where	nr_seq_paciente = nr_seq_paciente_p;

select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
into STRICT	ie_pessoa_w
from	atendimento_paciente
where	nr_atendimento	= nr_atendimento_p
and	cd_pessoa_fisica	= cd_pessoa_fisica_w;

if (ie_pessoa_w = 'S') then
	begin
	update	prescr_medica
	set	nr_atendimento	= nr_atendimento_p
	where	nr_prescricao	= nr_prescricao_p;

	update	paciente_atendimento
	set	nr_atendimento	= nr_atendimento_p
	where	nr_seq_atendimento	= nr_seq_atendimento_p;
	end;
	
	CALL CPOE_Vincular_atend_prescr(nr_prescricao_p,nr_atendimento_p, wheb_usuario_pck.get_nm_usuario);
else
	ds_erro_p	:= substr(obter_texto_tasy(59493, wheb_usuario_pck.get_nr_seq_idioma),1,255);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
