SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cons_regra_clinica_espec ( ie_tipo_atendimento_p bigint, cd_clinica_p bigint, cd_medico_p text, nm_usuario_p text) AS $body$
DECLARE

qt_itens_w	                bigint;
qt_itens_medico_w	bigint;
cd_estabelecimento_w	bigint;
ds_clinicas_w	                varchar(200);
ds_clinica_lib_w                      varchar(255);
ie_medico_sem_regra_w	varchar(1);
cd_perfil_w		bigint;

C01 CURSOR FOR
	SELECT 	substr(ds_valor_dominio,1,200)
	from	regra_cons_clinica_espec a,
		medico_especialidade b,
		valor_dominio c
	where	coalesce(a.ie_tipo_atendimento,ie_tipo_atendimento_p) = ie_tipo_atendimento_p
	and	a.cd_especialidade = b.cd_especialidade
	and	b.cd_pessoa_fisica = cd_medico_p
	and	coalesce(ie_forma_consistencia,'E') = 'E'
	and	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
	and	c.cd_dominio = 17
	and	c.vl_dominio = a.cd_clinica
	and	coalesce(a.cd_perfil_ativo, cd_perfil_w) = cd_perfil_w;

C02 CURSOR FOR
	SELECT 	ds_valor_dominio
	from	regra_cons_clinica_espec a,
		valor_dominio c
	where	coalesce(a.ie_tipo_atendimento,ie_tipo_atendimento_p) = ie_tipo_atendimento_p
	and	a.cd_medico_resp = cd_medico_p
	and	coalesce(ie_forma_consistencia,'E') = 'M'
	and	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
	and	c.cd_dominio = 17
	and	c.vl_dominio = a.cd_clinica
	and	coalesce(a.cd_perfil_ativo, cd_perfil_w) = cd_perfil_w;


BEGIN
cd_perfil_w := coalesce(wheb_usuario_pck.get_cd_perfil,0);
cd_estabelecimento_w	:= coalesce(wheb_usuario_pck.get_cd_estabelecimento,0);
ie_medico_sem_regra_w  := obter_param_usuario(916, 778, Obter_perfil_Ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_medico_sem_regra_w );

select 	count(*)
into STRICT	qt_itens_w
from	regra_cons_clinica_espec
where	coalesce(coalesce(cd_clinica,cd_clinica_p),0) = coalesce(cd_clinica_p,0)
and	coalesce(ie_tipo_atendimento,ie_tipo_atendimento_p) = ie_tipo_atendimento_p
and	coalesce(ie_forma_consistencia,'E') = 'E'
and	coalesce(cd_perfil_ativo, cd_perfil_w) = cd_perfil_w
and	coalesce(cd_estabelecimento,cd_estabelecimento_w)	= cd_estabelecimento_w;

if (qt_itens_w > 0) then


	select 	count(*)
	into STRICT	qt_itens_w
	from	regra_cons_clinica_espec a,
		medico_especialidade b
	where	coalesce(a.ie_tipo_atendimento,ie_tipo_atendimento_p) = ie_tipo_atendimento_p
	and	a.cd_especialidade = b.cd_especialidade
	and	coalesce(coalesce(cd_clinica,cd_clinica_p),0) = coalesce(cd_clinica_p,0)
	and	b.cd_pessoa_fisica	=	cd_medico_p
	and	coalesce(ie_forma_consistencia,'E') = 'E'
	and	coalesce(a.cd_perfil_ativo, cd_perfil_w) = cd_perfil_w
	and	coalesce(cd_estabelecimento,cd_estabelecimento_w)	= cd_estabelecimento_w;

	open C01;
	loop
	fetch 	C01 into
		ds_clinica_lib_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_clinicas_w := substr(ds_clinicas_w||ds_clinica_lib_w || ', ',1,200);
		end;
	end loop;
	close C01;

	if (ie_medico_sem_regra_w = 'S') then
		select 	count(*)
		into STRICT	qt_itens_medico_w
		from	regra_cons_clinica_espec a
		where	a.cd_medico_resp = cd_medico_p
		and	coalesce(a.cd_perfil_ativo, cd_perfil_w) = cd_perfil_w
		and	coalesce(cd_estabelecimento,cd_estabelecimento_w)	= cd_estabelecimento_w;
	end if;

	if (qt_itens_w = 0)
	And	((ie_medico_sem_regra_w = 'N') or (ie_medico_sem_regra_w = 'S' AND qt_itens_medico_w > 0)) then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(240443, 'DS_CLINICAS=' ||ds_clinicas_w);--O médico não possui especialidade liberada para esta clinica. Clínicas liberadas:
	end if;
end if;

select 	count(*)
into STRICT	qt_itens_w
from	regra_cons_clinica_espec
where	coalesce(coalesce(cd_clinica,cd_clinica_p),0) = coalesce(cd_clinica_p,0)
and	coalesce(ie_tipo_atendimento,ie_tipo_atendimento_p) = ie_tipo_atendimento_p
and	coalesce(ie_forma_consistencia,'E') = 'M'
and	coalesce(cd_perfil_ativo, cd_perfil_w) = cd_perfil_w
and	coalesce(cd_estabelecimento,cd_estabelecimento_w)	= cd_estabelecimento_w;


if (qt_itens_w > 0) then


	select count(*)
	into STRICT	qt_itens_w
	from	regra_cons_clinica_espec a
	where	coalesce(a.ie_tipo_atendimento,ie_tipo_atendimento_p) = ie_tipo_atendimento_p
	and	coalesce(coalesce(cd_clinica,cd_clinica_p),0) = coalesce(cd_clinica_p,0)
	and	a.cd_medico_resp	=	cd_medico_p
	and	coalesce(ie_forma_consistencia,'E') = 'M'
	and	coalesce(a.cd_perfil_ativo, cd_perfil_w) = cd_perfil_w
	and	coalesce(cd_estabelecimento,cd_estabelecimento_w)	= cd_estabelecimento_w;

	open C02;
	loop
	fetch C02 into
		ds_clinica_lib_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		ds_clinicas_w := ds_clinicas_w||ds_clinica_lib_w || ', ';
		end;
	end loop;
	close C02;

	if (ie_medico_sem_regra_w = 'S') then
		select 	count(*)
		into STRICT	qt_itens_medico_w
		from	regra_cons_clinica_espec a
		where	a.cd_medico_resp = cd_medico_p
		and	coalesce(a.cd_perfil_ativo, cd_perfil_w) = cd_perfil_w
		and	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w;
	end if;

	if (qt_itens_w = 0)
	And	((ie_medico_sem_regra_w = 'N') or (ie_medico_sem_regra_w = 'S' AND qt_itens_medico_w > 0)) then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(240444, 'DS_CLINICAS=' || ds_clinicas_w); --O médico não possui liberação para este tipo de atendimento/clínica. Clínicas liberadas:
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
