CREATE OR REPLACE PROCEDURE cons_regra_func_setor_atepacu (cd_setor_atendimento_p bigint, nr_atendimento_p bigint) AS $body$
DECLARE

 
ie_setor_regra_w		varchar(1);
cd_pessoa_fisica_w	varchar(10);
ie_atepacu_ok_w		varchar(1);
					

BEGIN 
if (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
		 
		/* obter dados atendimento */
 
		select	a.cd_pessoa_fisica 
		into STRICT	cd_pessoa_fisica_w 
		from	pessoa_fisica b, 
			atendimento_paciente a 
		where	b.cd_pessoa_fisica = a.cd_pessoa_fisica 
		and	a.nr_atendimento = nr_atendimento_p;
		 
		if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
 
		/* consistir regra funcionamento setor */
 
		 
		select	consistir_idade_comp_setor(cd_pessoa_fisica_w,cd_setor_atendimento_p) 
		into STRICT	ie_atepacu_ok_w	 
		;
		 
				 
			if (ie_atepacu_ok_w = 'N') then 
				CALL Wheb_mensagem_pck.exibir_mensagem_abort(262504);
			end if;
				 
		end if;
	 
end if;
 
end;
$body$
