CREATE OR REPLACE PROCEDURE cons_regra_num_controle_visita (ie_pasta_p text, cd_perfil_p bigint, nr_seq_controle_p text, ds_erro_p INOUT text) AS $body$
DECLARE


nr_seq_regra_w			bigint;
vl_maximo_controle_w	bigint;
vl_minimo_controle_w	bigint;
ds_erro_w				varchar(255) := '';

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	regra_num_controle_visita
	where	cd_perfil = cd_perfil_p
	and		coalesce(ie_situacao,'A') = 'A'
	and		((ie_tipo_visita   =  ie_pasta_p) or (ie_tipo_visita = 'T'));


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(nr_seq_regra_w,0) > 0)	then
		select	vl_minimo_controle,
			vl_maximo_controle
		into STRICT	vl_minimo_controle_w,
			vl_maximo_controle_w
		from	regra_num_controle_visita
		where	nr_sequencia	= nr_seq_regra_w;

		if (vl_minimo_controle_w > coalesce(somente_numero(nr_seq_controle_p), 0)) then
			ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(277685,'VL_MINIMO_CONTROLE='||vl_minimo_controle_w);
		else
			if (vl_maximo_controle_w < coalesce(somente_numero(nr_seq_controle_p), 0)) then
				ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(277685,'VL_MINIMO_CONTROLE='||vl_minimo_controle_w);
			end if;
		end if;
	end if;

	end;
end loop;
close C01;

ds_erro_p	:= ds_erro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
