CREATE OR REPLACE PROCEDURE conta_paciente_etapa_post ( nr_seq_etapa_conta_p bigint, ie_evento_p text, nr_interno_conta_p bigint, nr_seq_etapa_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
qt_existe_w	bigint;


BEGIN 
 
CALL Atualiza_Final_Etapa_Conta(nr_seq_etapa_conta_p, nm_usuario_p);
 
select	count(*) 
into STRICT	qt_existe_w 
from	fatur_etapa_alta 
where	ie_evento = 'K';
 
if (qt_existe_w > 0) then 
 
	CALL gerar_etapa_inserir_etapa(nr_interno_conta_p, nr_seq_etapa_p, nm_usuario_p);
	 
end if;
 
commit;
 
end;
$body$
