SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE controlar_processo_alta () AS $body$
DECLARE


nr_sequencia_w				bigint;
cd_processo_w				bigint;
nr_atendimento_w				bigint;
dt_fim_prev_w				timestamp;
ie_Processo_Pendente_w			varchar(1);
ie_baixa_Sistema_w			varchar(1);
ie_existe_w				bigint;

C001 CURSOR FOR
	SELECT 	a.nr_atendimento,
		b.nr_sequencia,
		b.cd_processo,
		b.dt_fim_prev,
		coalesce(c.ie_baixa_sistema,'N')
	from 	Atendimento_paciente a,
		Processo_atendimento b,
		Processo_Etapa c
	where 	a.nr_atendimento	= b.nr_atendimento
	and 	c.cd_processo	= b.cd_processo
	and 	c.nr_sequencia	= b.nr_sequencia
	and	a.dt_alta_interno <= clock_timestamp() + interval '100 days'
	and 	a.ie_fim_conta in ('A','P')
	and	c.ie_baixa_sistema = 'S'
	and	b.dt_fim_prev < clock_timestamp()
	and	b.ie_status <> 'F'
	and	coalesce(b.dt_fim_real::text, '') = '';




BEGIN

select 	max(1)
into STRICT	ie_existe_w
from 	Processo_atendimento
where	ie_status = 'P'  LIMIT 1;

if (ie_existe_w  = 1) then
	OPEN C001;
	LOOP
	FETCH C001 INTO 	nr_atendimento_w,
				nr_sequencia_w,
				cd_processo_w,
				dt_fim_prev_w,
				ie_baixa_sistema_w;
	EXIT WHEN NOT FOUND; /* apply on c001 */
		begin


		update 	processo_atendimento
		set 	dt_fim_real	= clock_timestamp(),
			ie_status	= 'F',
			nm_usuario	= 'Tasy'
		where nr_atendimento 	= nr_atendimento_w
		  and cd_processo    	= cd_processo_w
		  and nr_sequencia	= nr_sequencia_w;

		end;

	END LOOP;
	CLOSE C001;
	COMMIT;

End if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE controlar_processo_alta () FROM PUBLIC;

