CREATE OR REPLACE PROCEDURE controlar_recebimento_etapa ( nr_sequencia_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
BEGIN

if (ie_opcao_p = 'R') then
	update	conta_paciente_etapa
	set	dt_recebimento = clock_timestamp(),
		nm_usuario_recebimento = nm_usuario_p,
		ie_status = 'R'
	where	nr_sequencia = nr_sequencia_p;
elsif (ie_opcao_p = 'D') then
	update	conta_paciente_etapa
	set	dt_recebimento  = NULL,
		nm_usuario_recebimento  = NULL,
		ie_status = 'P'
	where	nr_sequencia = nr_sequencia_p;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
