CREATE OR REPLACE PROCEDURE controlar_transf_etapa_conta (nr_interno_conta_p bigint, nm_usuario_p text, nr_seq_etapa_p bigint, nr_motivo_devol_p bigint, ds_observacao_p text, cd_setor_atendimento_p bigint, ie_finaliza_etapas_ant_p text) AS $body$
DECLARE

 
ds_observacao_w     conta_paciente_etapa.ds_observacao%type;


BEGIN 
 
if (coalesce(ie_finaliza_etapas_ant_p,'N') = 'S') and (nr_interno_Conta_p IS NOT NULL AND nr_interno_Conta_p::text <> '') then 
	 
	update	conta_paciente_etapa 
	set	nm_usuario 		= nm_usuario_p, 
		dt_atualizacao 		= clock_timestamp(), 
		dt_fim_etapa		= clock_timestamp() 
	where	nr_interno_conta	= nr_interno_conta_p 
	and	coalesce(dt_fim_etapa::text, '') = '';
	 
end if;
 
if (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') and (nr_seq_etapa_p IS NOT NULL AND nr_seq_etapa_p::text <> '') then 
     
    ds_observacao_w := substr(ds_observacao_p,1,2000);
 
	CALL gerar_conta_etapa( 	nr_interno_conta_p, 
				nm_usuario_p, 
				nr_seq_etapa_p, 
				nr_motivo_devol_p, 
				cd_setor_atendimento_p, 
				ds_observacao_w);
	 
end if;
 
 
 
commit;
 
end;
$body$
