SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE controla_prioridade_tempo_pa ( cd_estabelecimento_p bigint) AS $body$
DECLARE

 
 
 
dt_entrada_w	timestamp;
nr_atendimento_w	bigint;
qt_tempo_min_w		bigint;
qt_idade_w		bigint;
nr_seq_prioridade_w	bigint;

 
C01 CURSOR FOR 
	SELECT	a.nr_atendimento, 
		a.dt_entrada, 
		trunc((clock_timestamp() - a.dt_entrada) * 24) * 60 , 
		a.qt_idade 
	from	atendimento_ps_v a 
	where	a.dt_entrada between clock_timestamp() - interval '2 days' and clock_timestamp() 
	and	coalesce(a.dt_alta::text, '') = '' 
	and	a.cd_estabelecimento	= cd_estabelecimento_p 
	and	coalesce(a.dt_atend_medico::text, '') = '';
	
	 
C02 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	TRIAGEM_CLASSIF_PRIORIDADE a 
	where	qt_idade_w between coalesce(a.qt_idade_min,0) and coalesce(a.qt_idade_max,999) 
	and	qt_tempo_min_w between a.QT_TEMPO_MIN and a.QT_TEMPO_MAX 
	and	coalesce(a.cd_estabelecimento,cd_estabelecimento_p)	= cd_estabelecimento_p 
	order by a.nr_seq_prioridade desc;
	
	 
	 
 

BEGIN 
open C01;
loop 
fetch C01 into	 
	nr_atendimento_w, 
	dt_entrada_w, 
	qt_tempo_min_w, 
	qt_idade_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	open C02;
	loop 
	fetch C02 into	 
		nr_seq_prioridade_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		if (nr_seq_prioridade_w IS NOT NULL AND nr_seq_prioridade_w::text <> '') then 
			update	atendimento_paciente 
			set	NR_SEQ_TRIAGEM_PRIORIDADE	= nr_seq_prioridade_w 
			where	nr_atendimento			= nr_atendimento_w;
			commit;
		end if;
		end;
	end loop;
	close C02;
 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
