CREATE OR REPLACE PROCEDURE controla_subsecao_linha_ref ( nr_sequencia_p documento_subsecao.nr_sequencia%type, nr_seq_documento_secao_p documento_subsecao.nr_seq_documento_secao%type, ie_change_reference_p text default 'S') AS $body$
