CREATE OR REPLACE PROCEDURE control_qtd_diet_enteral (nr_atendimento_p bigint, qtd_enteral_diet_p INOUT bigint) AS $body$
DECLARE


qtd_enteral_diet_max_w bigint := 3;


BEGIN

    SELECT COUNT(1)
    INTO STRICT qtd_enteral_diet_p
    FROM CPOE_DIETA
    WHERE IE_TIPO_DIETA = 'E' AND NR_ATENDIMENTO = nr_atendimento_p;

    IF (qtd_enteral_diet_p > qtd_enteral_diet_max_w) THEN
        UPDATE CPOE_DIETA SET IE_DIETA_ESPECIAL = 'N' WHERE IE_TIPO_DIETA = 'E' AND NR_ATENDIMENTO = nr_atendimento_p;
    END IF;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
