CREATE OR REPLACE PROCEDURE converter_conv_part_age_pac (nr_sequencia_p bigint, nr_seq_agenda_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE



cd_convenio_w	integer;


BEGIN
begin


select  min(cd_convenio)
into STRICT 	cd_convenio_w
from    convenio
where   ie_tipo_convenio = 1
and	ie_situacao =  'A';


if (ie_opcao_p = 'P') then

	if (coalesce(nr_sequencia_p,0) > 0) then

		update agenda_paciente
		set	cd_convenio		= cd_convenio_w,
		        cd_categoria 		 = NULL,
			cd_plano		 = NULL
		where	nr_sequencia		= nr_sequencia_p;
		commit;
	end if;

elsif (ie_opcao_p = 'A') then

	if ((coalesce(nr_sequencia_p,0) > 0) and (coalesce(nr_seq_agenda_p,0) > 0)) then

		update  agenda_paciente_proc
		set		cd_convenio   		= cd_convenio_w,
				cd_categoria 		 = NULL,
				cd_plano			 = NULL
		where 	nr_sequencia 	    = nr_sequencia_p
		and	nr_seq_agenda 		    = nr_seq_agenda_p;
		commit;
	end if;
end  if;

exception
	when others then
	null;
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
