CREATE OR REPLACE PROCEDURE con_consiste_vigencia_conta ( cd_conta_contabil_p text, dt_inicio_vigencia_p timestamp, dt_fim_vigencia_p timestamp, ds_consistencia_p INOUT text) AS $body$
DECLARE



qt_existe_vigencia_w		integer;
dt_inicio_vigencia_w		timestamp;
dt_fim_vigencia_w		timestamp;
ds_consistencia_w		varchar(4000);


BEGIN

select	count(*)
into STRICT	qt_existe_vigencia_w
from	conta_contabil
where	cd_conta_contabil = cd_conta_contabil_p
and	(dt_inicio_vigencia IS NOT NULL AND dt_inicio_vigencia::text <> '')
and	(dt_fim_vigencia IS NOT NULL AND dt_fim_vigencia::text <> '');

if (qt_existe_vigencia_w > 0) then
	begin

	if (coalesce(dt_inicio_vigencia_p::text, '') = '') or (coalesce(dt_fim_vigencia_p::text, '') = '') then
		ds_consistencia_w	:= wheb_mensagem_pck.get_texto(303801,'cd_conta_contabil_p=' || cd_conta_contabil_p);
	end if;

	select	dt_inicio_vigencia,
		dt_fim_vigencia
	into STRICT	dt_inicio_vigencia_w,
		dt_fim_vigencia_w
	from	conta_contabil
	where	cd_conta_contabil = cd_conta_contabil_p;

	if (dt_inicio_vigencia_p < dt_inicio_vigencia_w) then
		ds_consistencia_w	:= wheb_mensagem_pck.get_texto(303818, 'cd_conta_contabil_p=' || cd_conta_contabil_p || ';dt_inicio_vigencia_w=' || dt_inicio_vigencia_w ||  ';dt_inicio_vigencia_p=' || dt_inicio_vigencia_p);
	end if;
	if (dt_fim_vigencia_p > dt_fim_vigencia_w) then
		 ds_consistencia_w := wheb_mensagem_pck.get_texto(303839, 'cd_conta_contabil_p=' || cd_conta_contabil_p || ';dt_fim_vigencia_w=' || dt_fim_vigencia_w || ';dt_fim_vigencia_p=' || dt_fim_vigencia_p);
	end if;

	if (dt_fim_vigencia_p < dt_inicio_vigencia_p) then
		 ds_consistencia_w	:= wheb_mensagem_pck.get_texto(303842, 'dt_fim_vigencia_p=' || dt_fim_vigencia_p || ';dt_inicio_vigencia_p=' || dt_inicio_vigencia_p);
	end if;

	end;
end if;

if (coalesce(dt_inicio_vigencia_p::text, '') = '') and (dt_fim_vigencia_p IS NOT NULL AND dt_fim_vigencia_p::text <> '') then
	ds_consistencia_w	:= wheb_mensagem_pck.get_texto(303848);

elsif (dt_inicio_vigencia_p IS NOT NULL AND dt_inicio_vigencia_p::text <> '') and (coalesce(dt_fim_vigencia_p::text, '') = '') then
	ds_consistencia_w	:= wheb_mensagem_pck.get_texto(303848);
end if;

ds_consistencia_p	:= substr(ds_consistencia_w,1, 4000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
