CREATE OR REPLACE PROCEDURE con_inserir_clausula_padrao ( nr_seq_contrato_p bigint, nr_seq_tipo_p bigint, ds_titulo_p text, nr_seq_clausula_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
ds_clausula_w		varchar(4000);
ds_clausula_1_w		text;
ds_titulo_w		varchar(100);



BEGIN

ds_titulo_w	:= substr(ds_titulo_p,1,100);

select	ds_clausula,
	ds_clausula_ext
into STRICT	ds_clausula_w,
	ds_clausula_1_w
from	contrato_clausula_padrao
where	nr_sequencia = nr_seq_clausula_p;

ds_clausula_1_w :=  ds_clausula_w || chr(13) || ds_clausula_1_w;


select	nextval('contrato_clausula_seq')
into STRICT	nr_sequencia_w
;

insert into contrato_clausula(
	nr_sequencia,
	nr_seq_contrato,
	dt_atualizacao,
	nm_usuario,
	nr_seq_tipo,
	ds_titulo,
	ds_clausula)
values (	nr_sequencia_w,
	nr_seq_contrato_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_tipo_p,
	ds_titulo_w,
	ds_clausula_1_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
