CREATE OR REPLACE PROCEDURE con_inserir_regra_itens_nf ( nr_sequencia_p bigint, nr_seq_contrato_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

				
cd_estab_regra_w	contrato_regra_nf_estab.cd_estab_regra%type;


BEGIN
insert into contrato_regra_nf(nr_sequencia,
	nr_seq_contrato,
	dt_atualizacao,
	nm_usuario,
	cd_material,
	cd_conta_contabil,
	cd_centro_custo,
	nr_seq_crit_rateio,
	ds_complemento,
	ds_observacao,
	nr_seq_conta_financ,
	vl_pagto,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	dt_inicio_vigencia,
	dt_fim_vigencia,
	pr_variacao_valor,
	qt_conversao_nf,
	ie_conversao_nf,
	nr_seq_proj_rec,
	nr_seq_equipamento,
	cd_estab_regra,
	cd_local_estoque,
	dt_venc_pagto,
	qt_dias_antes_gerar_sc,
	ie_gera_sc_automatico,
	ie_tipo_regra,
	cd_pessoa_solicitante,
	ie_libera_solic,
	nr_seq_marca,
	vl_saldo_total,
	nr_cot_compra,
	nr_item_cot_compra,
	cd_natureza_operacao,
	cd_unidade_medida_compra,
	ie_preco,
	ie_quantidade,
    ie_situacao)
(SELECT	nextval('contrato_regra_nf_seq'),
	nr_seq_contrato_p,
	dt_atualizacao,
	nm_usuario_p,
	cd_material,
	cd_conta_contabil,
	cd_centro_custo,
	nr_seq_crit_rateio,
	ds_complemento,
	ds_observacao,
	nr_seq_conta_financ,
	vl_pagto,
	dt_atualizacao_nrec,
	nm_usuario_p,
	dt_inicio_vigencia,
	dt_fim_vigencia,
	pr_variacao_valor,
	qt_conversao_nf,
	ie_conversao_nf,
	nr_seq_proj_rec,
	nr_seq_equipamento,
	cd_estab_regra,
	cd_local_estoque,
	dt_venc_pagto,
	qt_dias_antes_gerar_sc,
	ie_gera_sc_automatico,
	ie_tipo_regra,
	cd_pessoa_solicitante,
	ie_libera_solic,
	nr_seq_marca,
	vl_saldo_total,
	nr_cot_compra,
	nr_item_cot_compra,
	cd_natureza_operacao,
	cd_unidade_medida_compra,
	ie_preco,
	ie_quantidade,
    ie_situacao
from 	contrato_regra_nf
where 	nr_sequencia = nr_sequencia_p);

select	max(cd_estab_regra)
into STRICT	cd_estab_regra_w
from 	contrato_regra_nf
where	nr_sequencia = nr_sequencia_p;

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (coalesce(cd_estab_regra_w,0) > 0) then
	insert into contrato_regra_nf_estab(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_regra_nf,
		cd_estab_regra)
	SELECT	nextval('contrato_regra_nf_estab_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_contrato_p,
		cd_estab_regra
	from	contrato_regra_nf_estab
	where	nr_seq_regra_nf = nr_sequencia_p;	
end if;

commit;

end;
$body$
