CREATE OR REPLACE PROCEDURE copiar_acomp_dia_anterior ( cd_setor_atendimento_p bigint, dt_dieta_p timestamp, cd_refeicao_p text, nm_usuario_p text ) AS $body$
DECLARE

 
nr_sequencia_w		bigint;
dt_dieta_w		timestamp;
ie_setor_atual_w	varchar(1);

C01 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	mapa_dieta a 
	where	coalesce(a.nr_seq_superior::text, '') = '' 
	and	a.dt_dieta = dt_dieta_w 
	and	a.ie_destino_dieta 	 = 'A' 
	and	a.cd_refeicao 		 = cd_refeicao_p 
	and	((a.cd_setor_atendimento = cd_setor_atendimento_p) or (cd_setor_atendimento_p = 0)) 
	and	not exists(	SELECT	1 
				from	mapa_dieta x 
				where	coalesce(x.nr_seq_superior::text, '') = '' 
				and	x.dt_dieta 		= dt_dieta_p 
				and	x.ie_destino_dieta 	= 'A' 
				and	x.cd_refeicao 		= cd_refeicao_p 
				and	((x.cd_setor_atendimento = cd_setor_atendimento_p) or (cd_setor_atendimento_p = 0)) 
				and	a.nr_atendimento	= x.nr_atendimento) 
	and	((Obter_Setor_Atendimento(a.nr_atendimento) = cd_setor_atendimento_p) or (ie_setor_atual_w = 'N'));			

BEGIN 
dt_dieta_w	:= dt_dieta_p-1;
ie_setor_atual_w := obter_param_usuario(1000, 117, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_setor_atual_w);
 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	insert into mapa_dieta( 
		nr_sequencia, 
		cd_pessoa_fisica, 
		dt_dieta, 
		cd_refeicao, 
		ie_destino_dieta, 
		ie_status, 
		dt_atualizacao, 
		nm_usuario, 
		cd_setor_atendimento, 
		cd_unidade_basica, 
		cd_unidade_compl, 
		cd_dieta, 
		ds_observacao, 
		nr_atendimento, 
		qt_parametro, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec) 
	SELECT	nextval('mapa_dieta_seq'), 
		cd_pessoa_fisica, 
		dt_dieta_p, 
		cd_refeicao_p, 
		ie_destino_dieta, 
		ie_status, 
		clock_timestamp(), 
		nm_usuario_p, 
		cd_setor_atendimento_p, 
		cd_unidade_basica, 
		cd_unidade_compl, 
		cd_dieta, 
		ds_observacao, 
		nr_atendimento, 
		qt_parametro, 
		clock_timestamp(), 
		nm_usuario_p 
	from	mapa_dieta 
	where	nr_sequencia = nr_sequencia_w;
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
