CREATE OR REPLACE PROCEDURE copiar_agenda_exame_sel ( nr_seq_origem_p bigint, nr_seq_destino_p bigint, ie_acao_p text, cd_estabelecimento_p bigint, cd_perfil_p bigint, nm_usuario_p text, ds_mensagem_p INOUT text, ds_consistencia_p INOUT text, ds_erro_p INOUT text, ie_medico_p INOUT text) AS $body$
DECLARE

 
ds_consistencia_w	varchar(255);
ds_erro_w		varchar(255);
ie_medico_w		varchar(1);
ds_mensagem_w		varchar(255);
vl_parametro_w		varchar(255);
qt_aut_vig_w		bigint;
					

BEGIN 
 
if (nr_seq_origem_p > 0) and (nr_seq_destino_p > 0)then 
	begin 
	SELECT * FROM Consistir_transf_agenda(nr_seq_origem_p, nr_seq_destino_p, ie_acao_p, cd_perfil_p, cd_estabelecimento_p, nm_usuario_p, ds_consistencia_w, ds_erro_w, ie_medico_w) INTO STRICT ds_consistencia_w, ds_erro_w, ie_medico_w;
 
	vl_parametro_w := Obter_Param_Usuario(820, 126, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, vl_parametro_w);
			  
	if (vl_parametro_w = 'S') then 
		begin	 
		qt_aut_vig_w	:= obter_qt_aut_vigencia(nr_seq_origem_p, nr_seq_destino_p);
		if (qt_aut_vig_w > 0) then 
			ds_mensagem_w	:= substr(obter_texto_tasy(42578, wheb_usuario_pck.get_nr_seq_idioma),1,255);	
		end if;	
		end;
	end if;
	end;
end if;
	 
ds_consistencia_p	:= ds_consistencia_w;	
ds_erro_p		:= ds_erro_w;
ie_medico_p		:= ie_medico_w;
ds_mensagem_p		:= ds_mensagem_w;
 
end;
$body$
