CREATE OR REPLACE PROCEDURE copiar_ativ_planej_prev ( nr_sequencia_p bigint, nr_seq_planej_copia_p bigint, nm_usuario_p text) AS $body$
DECLARE



ds_atividade_w					varchar(2000);
qt_min_prev_w					bigint;
nr_seq_apres_w					integer;

C01 CURSOR FOR
	SELECT	ds_atividade,
		qt_min_prev,
		nr_seq_apres
	into STRICT	ds_atividade_w,
		qt_min_prev_w,
		nr_seq_apres_w
	from	man_planej_atividade
	where	nr_seq_planej_prev = nr_seq_planej_copia_p
	order by 1;


BEGIN
open C01;
loop
fetch C01 into
	ds_atividade_w,
	qt_min_prev_w,
	nr_seq_apres_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	insert into	man_planej_atividade(	nr_sequencia,
						nr_seq_planej_prev,
						dt_atualizacao,
						nm_usuario,
						ds_atividade,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						qt_min_prev,
						nr_seq_apres)
	values (nextval('man_planej_atividade_seq'),
			nr_sequencia_p,
			clock_timestamp(),
			nm_usuario_p,
			ds_atividade_w,
			clock_timestamp(),
			nm_usuario_p,
			qt_min_prev_w,
			nr_seq_apres_w);

	end;
end loop;
