CREATE OR REPLACE PROCEDURE copiar_atributo_visao ( nm_tabela_p text, nm_atributo_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	tabela_visao
	where	nm_tabela	= nm_tabela_p;

BEGIN

OPEN C01;
LOOP
FETCH C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	insert into tabela_visao_atributo(nr_sequencia,
		nm_atributo,
		dt_atualizacao,
		nm_usuario,
		vl_padrao,
		ds_mascara,
		ie_readonly,
		ie_tabstop,
		qt_altura,
		cd_dominio,
		nr_seq_localizador,
		ds_valores,
		nr_seq_ordem,
		qt_coluna,
		ie_criar_descricao,
		ie_componente,
		nm_atributo_pai,
		ds_label_longo,
		ie_dbl_click_readonly,
		ie_tipo_botao,
		cd_exp_label,
		cd_exp_label_grid,
		cd_exp_valores)
	SELECT	nr_sequencia_w,
		nm_atributo,
		clock_timestamp(),
		nm_usuario,
		vl_default,
		ds_mascara,
		ie_readonly,
		ie_tabstop,
		qt_altura,
		cd_dominio,
		nr_seq_localizador,
		ds_valores,
		nr_seq_ordem,
		qt_coluna,
		ie_criar_descricao,
		ie_componente,
		nm_atributo_pai,
		ds_label_longo,
		ie_dbl_click_readonly,
		ie_tipo_botao,
		cd_exp_label,
		cd_exp_label_grid,
		cd_exp_valores
	from	tabela_atributo
	where	nm_tabela	= nm_tabela_p
	and	nm_atributo	= nm_atributo_p
	and not exists (SELECT	b.nm_atributo
		from	tabela_visao_atributo b
		where 	b.nr_sequencia	= nr_sequencia_w
		and	b.nm_atributo	= nm_atributo_p);
	end;
END LOOP;
CLOSE C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
