CREATE OR REPLACE PROCEDURE copiar_caixa_usuario (nm_usuario_origem_p text, nm_usuario_destino_p text, ie_limpa_reg_p text, nm_usuario_p text) AS $body$
DECLARE


nm_usuario_param_w	varchar(15);
cd_perfil_w		integer;
nr_seq_caixa_w		bigint;
ie_atualizar_w		varchar(1);
cd_estabelecimento_w	bigint;

c01 CURSOR FOR
SELECT	a.cd_estabelecimento,
	a.cd_perfil,
	a.nr_seq_caixa,
	a.ie_atualizar
from	usuario_caixa a
where	a.nm_usuario_param	= nm_usuario_origem_p
and	not exists (select	1
	from	usuario_caixa x
	where	x.nm_usuario_param	= nm_usuario_destino_p
	and	x.cd_perfil		= a.cd_perfil
	and	x.nr_seq_caixa		= a.nr_seq_caixa
	and	x.ie_atualizar		= a.ie_atualizar
	and	x.cd_estabelecimento	= a.cd_estabelecimento);


BEGIN

if (ie_limpa_reg_p = 'S') then
	delete	from usuario_caixa
	where	nm_usuario_param = nm_usuario_destino_p;
end if;

open C01;
loop
fetch C01 into
	cd_estabelecimento_w,
	cd_perfil_w,
	nr_seq_caixa_w,
	ie_atualizar_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	insert into usuario_caixa(nr_sequencia,
		cd_estabelecimento,
		nm_usuario_param,
		nr_seq_caixa,
		dt_atualizacao,
		nm_usuario,
		ie_atualizar,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_perfil)
	values (nextval('usuario_caixa_seq'),
		cd_estabelecimento_w,
		nm_usuario_destino_p,
		nr_seq_caixa_w,
		clock_timestamp(),
		nm_usuario_p,
		ie_atualizar_w,
		clock_timestamp(),
		nm_usuario_p,
		cd_perfil_w);

end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
