CREATE OR REPLACE PROCEDURE copiar_centro_custo_material ( nm_usuario_p text, cd_centro_original_p bigint, cd_centro_destino_p INOUT bigint, ie_sobrescrever_regra_p text, nr_seq_centro_material_p bigint) AS $body$
DECLARE



qt_centro_original_w	bigint;
qt_centro_copia_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_centro_original_w
from	centro_custo_material a
where	a.cd_centro_custo	= cd_centro_original_p
and	a.nr_sequencia	= coalesce(nr_seq_centro_material_p,a.nr_sequencia);

if (ie_sobrescrever_regra_p = 'S') and (qt_centro_original_w > 0)  then
	delete	FROM centro_custo_material a
	where	a.cd_centro_custo = cd_centro_destino_p;
end if;

select	count(*)
into STRICT	qt_centro_copia_w
from	centro_custo_material a
where	a.cd_centro_custo = cd_centro_destino_p;

if (qt_centro_original_w > 0) and (qt_centro_copia_w = 0) or (coalesce(nr_seq_centro_material_p,0) <> 0) then
	begin
	insert	into centro_custo_material(
		nr_sequencia,
		cd_centro_custo,
		dt_atualizacao,
		nm_usuario,
		cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material,
		cd_material,
		ie_requisicao,
		ie_exclui,
		cd_local_estoque,
		cd_operacao_estoque,
		ie_solicitacao,
		nr_seq_familia,
		qt_maxima)
	SELECT	nextval('centro_custo_material_seq'),
		cd_centro_destino_p,
		clock_timestamp(),
		nm_usuario_p,
		a.cd_grupo_material,
		a.cd_subgrupo_material,
		a.cd_classe_material,
		a.cd_material,
		a.ie_requisicao,
		a.ie_exclui,
		a.cd_local_estoque,
		a.cd_operacao_estoque,
		a.ie_solicitacao,
		a.nr_seq_familia,
		a.qt_maxima
	from	centro_custo_material a
	where	a.cd_centro_custo	= cd_centro_original_p
	and	a.nr_sequencia	= coalesce(nr_seq_centro_material_p, a.nr_sequencia);
	end;
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
