CREATE OR REPLACE PROCEDURE copiar_controle_pessoa ( CD_TIPO_CONTROLE_ORIGEM_P bigint, CD_TIPO_CONTROLE_DESTINO_P bigint, NM_USUARIO_P text) AS $body$
DECLARE


NR_SEQUENCIA_W		bigint := 0;
NR_SEQUENCIA_ATUAL_W		bigint := 0;


C02 CURSOR FOR
SELECT 	A.NR_SEQUENCIA
FROM 	CONTROLE_PESSOA A
WHERE 	A.CD_TIPO_CONTROLE = CD_TIPO_CONTROLE_ORIGEM_P
AND 	NOT EXISTS (	SELECT 	1
			FROM 	CONTROLE_PESSOA B
			WHERE 	CD_TIPO_CONTROLE = CD_TIPO_CONTROLE_DESTINO_P
			AND (A.CD_PESSOA_FISICA = B.CD_PESSOA_FISICA OR
					 A.CD_CGC = B.CD_CGC));


BEGIN
OPEN C02;
LOOP
FETCH C02
INTO NR_SEQUENCIA_W;
EXIT WHEN NOT FOUND; /* apply on C02 */
	BEGIN
	SELECT 	nextval('controle_pessoa_seq')
	INTO STRICT 	NR_SEQUENCIA_ATUAL_W
	;

	INSERT INTO CONTROLE_PESSOA(NR_SEQUENCIA,
		CD_TIPO_CONTROLE,
		DT_ATUALIZACAO,
		NM_USUARIO,
		CD_PESSOA_FISICA,
	 	CD_CGC,
		CD_CLASSIF_PRINC,
		CD_CLASSIF_SEC,
		NR_SEQ_CLIENTE,
		NR_SEQ_COM_CLIENTE,
	 	NM_PESSOA_CONTATO,
		IE_SITUACAO,
		CD_PF_RESP,
		DS_OBSERVACAO)
	(SELECT NR_SEQUENCIA_ATUAL_W,
		CD_TIPO_CONTROLE_DESTINO_P,
		clock_timestamp(),
		NM_USUARIO_P,
		CD_PESSOA_FISICA,
	 	CD_CGC,
		CD_CLASSIF_PRINC,
		CD_CLASSIF_SEC,
		NR_SEQ_CLIENTE,
		NR_SEQ_COM_CLIENTE,
	 	NM_PESSOA_CONTATO,
		IE_SITUACAO,
		CD_PF_RESP,
		DS_OBSERVACAO
	 FROM CONTROLE_PESSOA
	 WHERE NR_SEQUENCIA = NR_SEQUENCIA_W);
	END;
END LOOP;
CLOSE C02;

COMMIT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
