CREATE OR REPLACE PROCEDURE copiar_conv_meio_externo_pj ( cd_cnpj_origem_p text, cd_cnpj_destino_p text, nm_usuario_p text) AS $body$
BEGIN

if (cd_cnpj_origem_p IS NOT NULL AND cd_cnpj_origem_p::text <> '') and (cd_cnpj_destino_p IS NOT NULL AND cd_cnpj_destino_p::text <> '') and (cd_cnpj_origem_p <> cd_cnpj_destino_p) then
	begin

	insert into conversao_meio_externo(
		cd_cgc,
		nm_tabela,
		nm_atributo,
		cd_interno,
		cd_externo,
		dt_atualizacao,
		nm_usuario,
		nr_sequencia,
		ie_sistema_externo,
		nr_seq_regra,
		IE_ENVIO_RECEB)
	SELECT	cd_cnpj_destino_p,
		nm_tabela,
		nm_atributo,
		cd_interno,
		cd_externo,
		clock_timestamp(),
		nm_usuario_p,
		nextval('conversao_meio_externo_seq'),
		ie_sistema_externo,
		nr_seq_regra,
		coalesce(IE_ENVIO_RECEB, 'A')
	from	conversao_meio_externo
	where	cd_cgc	= cd_cnpj_origem_p;

	commit;
	end;
end if;




end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
