CREATE OR REPLACE PROCEDURE copiar_dados_turnos_agserv ( cd_agenda_p bigint, ds_lista_turnos_p text, ie_dia_semana_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ie_copia_convenios_turno_p text, ie_copia_profissionais_turno_p text, ie_copia_classif_liberadas_p text) AS $body$
DECLARE


ds_lista_turnos_w	varchar(2000);
nr_pos_virgula_w	bigint;
nr_seq_turno_w		bigint;

BEGIN
if (ds_lista_turnos_p IS NOT NULL AND ds_lista_turnos_p::text <> '') and (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	ds_lista_turnos_w	:= ds_lista_turnos_p;
	
	while(ds_lista_turnos_w IS NOT NULL AND ds_lista_turnos_w::text <> '') loop
		begin
		nr_pos_virgula_w	:= position(',' in ds_lista_turnos_w);
		if (nr_pos_virgula_w > 0) then
			begin
			nr_seq_turno_w		:= substr(ds_lista_turnos_w,0,nr_pos_virgula_w-1);
			ds_lista_turnos_w	:= substr(ds_lista_turnos_w,nr_pos_virgula_w+1,length(ds_lista_turnos_w));			
			end;
		else
			begin
			nr_seq_turno_w		:= (ds_lista_turnos_w)::numeric;
			ds_lista_turnos_w	:= null;
			end;
		end if;	
		
		if (coalesce(nr_seq_turno_w,0) > 0) then
			begin
			
			CALL copiar_dados_turno_agserv(
				cd_estabelecimento_p,
				cd_agenda_p,
				ie_dia_semana_p,
				nr_seq_turno_w,
				nm_usuario_p,
				ie_copia_convenios_turno_p,
				ie_copia_profissionais_turno_p,
				ie_copia_classif_liberadas_p);
			end;
		end if;
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
