CREATE OR REPLACE PROCEDURE copiar_escala_cadastro (nr_seq_ecala_dest_p bigint, nr_seq_ecala_orig_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_escala_w	bigint;


BEGIN

if	(nr_seq_ecala_orig_p > 0 AND nr_seq_ecala_dest_p > 0) then


	insert into escala_perfil(nr_sequencia,
					nr_seq_escala,
					dt_atualizacao,
					nm_usuario,
					cd_perfil,
					dt_atualizacao_nrec,
					nm_usuario_nrec)
				SELECT	nextval('escala_perfil_seq'),
					nr_seq_ecala_dest_p,
					clock_timestamp(),
					nm_usuario_p,
					cd_perfil,
					clock_timestamp(),
					nm_usuario_p
				from	escala_perfil
				where	nr_seq_escala = nr_seq_ecala_orig_p;

	insert into escala_horario(nr_sequencia,
					nr_seq_escala,
					dt_atualizacao,
					nm_usuario,
					ie_dia_semana,
					hr_inicial,
					hr_final,
					ie_feriado,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					dt_inicio_vigencia)
				SELECT	nextval('escala_horario_seq'),
					nr_seq_ecala_dest_p,
					clock_timestamp(),
					nm_usuario_p,
					ie_dia_semana,
					hr_inicial,
					hr_final,
					ie_feriado,
					clock_timestamp(),
					nm_usuario_p,
					dt_inicio_vigencia
				from	escala_horario
				where	nr_seq_escala = nr_seq_ecala_orig_p;



	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
