CREATE OR REPLACE PROCEDURE copiar_escala ( nr_seq_orig_p bigint, nr_seq_dest_p bigint, nm_usuario_p text, ie_copia_adicional_p text) AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
cd_pessoa_origem_w	varchar(10);
cd_pessoa_adic_w		varchar(20);
ds_observacao_w		varchar(255);
nr_seq_adic_w		bigint;
nr_seq_classif_escala_w	bigint;
nr_ordem_escala_w		bigint;
nr_seq_ordem_w			bigint;
nr_seq_equipe_w			bigint;
ds_obs_esc_diar_adic_w	varchar(255);

c01 CURSOR FOR
	SELECT	cd_pessoa_fisica,
		nr_seq_classif_escala,
		nr_ordem_escala,
		ds_observacao
	from	escala_diaria_adic
	where	nr_seq_escala_diaria	= nr_seq_orig_p;


BEGIN

select	cd_pessoa_fisica,
	coalesce(cd_pessoa_origem,cd_pessoa_fisica),
	ds_observacao,
	nr_ordem_escala,
	nr_seq_equipe
into STRICT	cd_pessoa_fisica_w,
	cd_pessoa_origem_w,
	ds_observacao_w,
	nr_seq_ordem_w,
	nr_seq_equipe_w
from	escala_diaria
where	nr_sequencia	= nr_seq_orig_p;

update	escala_diaria
set		cd_pessoa_fisica		= cd_pessoa_fisica_w,
		cd_pessoa_origem		= cd_pessoa_origem_w,
		ds_observacao			= ds_observacao_w,
		dt_atualizacao			= clock_timestamp(),
		nm_usuario				= nm_usuario_p,
		nr_ordem_escala			= nr_seq_ordem_w,
		nr_seq_equipe			= nr_seq_equipe_w
where	nr_sequencia		= nr_seq_dest_p;

if (coalesce(ie_copia_adicional_p, 'N') = 'S') then

	open c01;
	loop
	fetch c01 into
		cd_pessoa_adic_w,
		nr_seq_classif_escala_w,
		nr_ordem_escala_w,
		ds_obs_esc_diar_adic_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

		select	nextval('escala_diaria_adic_seq')
		into STRICT	nr_seq_adic_w
		;

		insert	into escala_diaria_adic(nr_sequencia,
			nr_seq_escala_diaria,
			dt_atualizacao,
			nm_usuario,
			cd_pessoa_fisica,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_classif_escala,
			nr_ordem_escala,
			ds_observacao)
		values (nr_seq_adic_w,
			nr_seq_dest_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_pessoa_adic_w,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_classif_escala_w,
			nr_ordem_escala_w,
			ds_obs_esc_diar_adic_w);

	end loop;
	close c01;

end if;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
