CREATE OR REPLACE PROCEDURE copiar_executar_lib_estab ( cd_estab_origem_p bigint, cd_estab_destino_p bigint, nr_seq_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	regra_exec_lib_prescr_mat
	where	cd_estabelecimento = cd_estab_origem_p
	and		nr_sequencia = nr_seq_p;

c01_w	c01%rowtype;


BEGIN
open C01;
loop
fetch C01 into
	nr_seq_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select 	nextval('regra_exec_lib_prescr_mat_seq')
	into STRICT	nr_sequencia_w
	;

			insert into regra_exec_lib_prescr_mat(
				cd_classe_material,
				cd_convenio,
				cd_estabelecimento,
				cd_grupo_material,
				cd_kit_material,
				cd_material,
				cd_perfil,
				cd_protocolo,
				cd_setor_atendimento,
				cd_subgrupo_material,
				cd_tipo_baixa,
				cd_unidade_medida_dose,
				ds_observacao,
				dt_atualizacao,
				dt_atualizacao_nrec,
				ie_acm,
				ie_cobrar_diluicao,
				ie_consistido_agenda,
				ie_liberacao,
				ie_sn,
				ie_urgencia,
				nm_usuario,
				nm_usuario_nrec,
				nr_seq_estrutura,
				nr_seq_sub_protocolo,
				nr_sequencia)
			SELECT	cd_classe_material,
				cd_convenio,
				cd_estab_destino_p,
				cd_grupo_material,
				cd_kit_material,
				cd_material,
				cd_perfil,
				cd_protocolo,
				cd_setor_atendimento,
				cd_subgrupo_material,
				cd_tipo_baixa,
				cd_unidade_medida_dose,
				ds_observacao,
				dt_atualizacao,
				dt_atualizacao_nrec,
				ie_acm,
				ie_cobrar_diluicao,
				ie_consistido_agenda,
				ie_liberacao,
				ie_sn,
				ie_urgencia,
				nm_usuario,
				nm_usuario_nrec,
				nr_seq_estrutura,
				nr_seq_sub_protocolo,
				nr_sequencia_w
			from	regra_exec_lib_prescr_mat
			where	nr_sequencia = nr_seq_w;


			insert 	into regra_exec_lib_prescr_dil(
				cd_material,
				dt_atualizacao,
				dt_atualizacao_nrec,
				nm_usuario,
				nm_usuario_nrec,
				nr_seq_regra,
				nr_sequencia)
			SELECT	cd_material,
				dt_atualizacao,
				dt_atualizacao_nrec,
				nm_usuario,
				nm_usuario_nrec,
				nr_sequencia_w,
				nextval('regra_exec_lib_prescr_dil_seq')
			from	regra_exec_lib_prescr_dil
			where	nr_seq_regra =  nr_seq_w;

			insert 	into rep_excluir_mat_conta(
				cd_setor_atendimento,
				dt_atualizacao,
				dt_atualizacao_nrec,
				nm_usuario,
				nm_usuario_nrec,
				nr_seq_regra,
				nr_sequencia)
			SELECT	cd_setor_atendimento,
				dt_atualizacao,
				dt_atualizacao_nrec,
				nm_usuario,
				nm_usuario_nrec,
				nr_sequencia_w,
				nextval('rep_excluir_mat_conta_seq')
			from	rep_excluir_mat_conta
			where	nr_seq_regra =  nr_seq_w;
	end;
end loop;
close C01;
commit;

end;
$body$
