CREATE OR REPLACE PROCEDURE copiar_feriado_estab ( cd_estab_origem_p bigint, cd_estab_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w		bigint;
dt_feriado_w		feriado.dt_feriado%type;
ds_motivo_feriado_w	feriado.ds_motivo_feriado%type;
ie_tipo_feriado_w	feriado.ie_tipo_feriado%type;


c01 CURSOR FOR
	SELECT	dt_feriado,
		ds_motivo_feriado,
		ie_tipo_feriado
	from	feriado
	where	cd_estabelecimento = cd_estab_origem_p;


BEGIN

open C01;
loop
fetch C01 into	
	dt_feriado_w,
	ds_motivo_feriado_w,
	ie_tipo_feriado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_existe_w
	from	feriado
	where	dt_feriado = dt_feriado_w
	and	cd_estabelecimento = cd_estab_destino_p;
	
	if (qt_existe_w = 0) then
		begin
		insert into feriado(
			cd_estabelecimento,
			dt_feriado,
			ds_motivo_feriado,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_tipo_feriado)
		values ( cd_estab_destino_p,
			dt_feriado_w,
			ds_motivo_feriado_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			ie_tipo_feriado_w);
		end;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
