CREATE OR REPLACE PROCEDURE copiar_hist_padrao_empresa ( cd_empresa_origem_p bigint, cd_empresa_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_historico_w			bigint;
ds_historico_w			varchar(255);

C01 CURSOR FOR
SELECT	ds_historico
from	historico_padrao
where	cd_empresa = cd_empresa_origem_p
and	ie_situacao = 'A';


BEGIN

open C01;
loop
fetch C01 into
	ds_historico_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(cd_historico) + 1
	into STRICT	cd_historico_w
	from	historico_padrao;

	insert into historico_padrao(
		cd_historico,
		ds_historico,
		dt_atualizacao,
		nm_usuario,
		cd_empresa,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_situacao)
	values (cd_historico_w,
		ds_historico_w,
		clock_timestamp(),
		nm_usuario_p,
		cd_empresa_destino_p,
		clock_timestamp(),
		nm_usuario_p,
		'A');
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
