SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE copiar_itens_apap (nr_seq_apap_origem_p bigint, nr_seq_apap_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_apap_grupo_w		bigint;

nr_seq_grupo_new_w		bigint;


c01 CURSOR FOR
SELECT	nr_sequencia
from	pep_apap_grupo
where	nr_seq_apap = nr_seq_apap_origem_p;



BEGIN

if (coalesce(nr_seq_apap_origem_p::text, '') = '' ) or (coalesce(nr_seq_apap_destino_p::text, '') = '' ) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(218437);

else
	open c01;
	loop
	fetch c01 into
		 nr_seq_apap_grupo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		select 	nextval('pep_apap_grupo_seq')
		into STRICT 	nr_seq_grupo_new_w
		;

		insert into pep_apap_grupo(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_apap,
			ds_grupo,
			nr_seq_apres,
			ie_tipo,
			ds_cor_grupo,
			ie_manter_nulo,
			ie_ganho_perda)
		SELECT	nr_seq_grupo_new_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_apap_destino_p,
			ds_grupo,
			nr_seq_apres,
			ie_tipo,
			ds_cor_grupo,
			ie_manter_nulo,
			coalesce(ie_ganho_perda,'N')
		from	pep_apap_grupo
		where	nr_sequencia	= nr_seq_apap_grupo_w;

		insert into pep_apap_inf(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_apap,
			nr_seq_apres,
			nr_seq_inf,
			nr_seq_grupo_apap,
			nr_seq_grupo_pg,
			nr_seq_tipo_pg,
			nr_ficha_tecnica,
			ie_tipo_dosagem,
			ie_balanco_hidrico,
			ie_manter_nulo,
			ie_grafico)		--Ivan em 16/04/2008 OS89705
		SELECT	nextval('pep_apap_inf_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			null,
			nr_seq_apres,
			nr_seq_inf,
			nr_seq_grupo_new_w,
			nr_seq_grupo_pg,
			nr_seq_tipo_pg,
			nr_ficha_tecnica,
			ie_tipo_dosagem,
			ie_balanco_hidrico,
			ie_manter_nulo,
			ie_grafico 		--Ivan em 16/04/2008 OS89705
		from	pep_apap_inf
		where	nr_seq_grupo_apap	= nr_seq_apap_grupo_w;

		end;
	end loop;
	close c01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE copiar_itens_apap (nr_seq_apap_origem_p bigint, nr_seq_apap_destino_p bigint, nm_usuario_p text) FROM PUBLIC;

