CREATE OR REPLACE PROCEDURE copiar_just_prot_atend_onc ( nr_seq_paciente_orig_p bigint, nr_seq_atendimento_dest_p bigint, nr_seq_material_orig_p bigint, nr_seq_material_dest_p bigint) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	ds_justif_incons_quimio,
			nr_seq_regra,
			qt_dose_prescricao
	from	paciente_atend_erro_just
	where	nr_seq_material = nr_seq_material_orig_p
	and		nr_seq_paciente = nr_seq_paciente_orig_p
	order by nr_seq_regra;

nr_seq_regra_w		bigint;
ds_justificativa_w	varchar(255);
qt_dose_prescricao_w	double precision;
nm_usuario_w		varchar(255);


BEGIN

if (nr_seq_paciente_orig_p IS NOT NULL AND nr_seq_paciente_orig_p::text <> '') and (nr_seq_atendimento_dest_p IS NOT NULL AND nr_seq_atendimento_dest_p::text <> '') and (nr_seq_material_orig_p IS NOT NULL AND nr_seq_material_orig_p::text <> '') and (nr_seq_material_dest_p IS NOT NULL AND nr_seq_material_dest_p::text <> '')then

	begin

	nm_usuario_w := wheb_usuario_pck.get_nm_usuario;

	open C01;
	loop
	fetch C01 into
		ds_justificativa_w,
		nr_seq_regra_w,
		qt_dose_prescricao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin


		insert into paciente_atend_erro_just(nr_sequencia,
											 nr_seq_atendimento,
											 nr_seq_material,
											 nm_usuario,
											 nm_usuario_nrec,
											 dt_atualizacao,
											 dt_atualizacao_nrec,
											 ds_justif_incons_quimio,
											 nr_seq_regra,
											 qt_dose_prescricao)
									values ( nextval('paciente_atend_erro_just_seq'),
											nr_seq_atendimento_dest_p,
											nr_seq_material_dest_p,
											nm_usuario_w,
											nm_usuario_w,
											clock_timestamp(),
											clock_timestamp(),
											ds_justificativa_w,
											nr_seq_regra_w,
											qt_dose_prescricao_w);

		commit;

		end;
	end loop;
	close C01;

	exception
		when others then
			null;
	end;

end if;

end;
$body$
