CREATE OR REPLACE PROCEDURE copiar_lib_estoque_usuario ( nm_usuario_origem_p text, nm_usuario_destino_p text, nm_usuario_p text) AS $body$
DECLARE


cd_estabelecimento_w	smallint;
cd_local_estoque_w	smallint;
ie_evento_w		varchar(2);
cd_perfil_w		integer;
cd_cargo_lib_w		bigint;
ie_permissao_w		varchar(1);
nr_seq_regra_evento_w	bigint;
nm_usuario_lib_w	varchar(15);


C01 CURSOR FOR
	SELECT	c.cd_perfil,
		c.cd_cargo_lib,
		c.ie_permissao,
		c.nr_seq_regra_evento
	from	regra_local_usuario a,
		evento_local_usuario b,
		permissao_local_usuario c
	where	a.nr_sequencia	= b.nr_seq_regra_local
	and	b.nr_sequencia	= c.nr_seq_regra_evento
	and	c.nm_usuario_lib = nm_usuario_origem_p;


BEGIN

open C01;
loop
fetch C01 into
	cd_perfil_w,
	cd_cargo_lib_w,
	ie_permissao_w,
	nr_seq_regra_evento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	insert into permissao_local_usuario(   nr_sequencia,
						nr_seq_regra_evento,
						nm_usuario_nrec,
						nm_usuario_lib,
						nm_usuario,
						ie_permissao,
						dt_atualizacao_nrec,
						dt_atualizacao,
						cd_perfil,
						cd_cargo_lib)
				values (	nextval('permissao_local_usuario_seq'),
						nr_seq_regra_evento_w,
						nm_usuario_p,
						nm_usuario_destino_p,
						nm_usuario_p,
						ie_permissao_w,
						clock_timestamp(),
						clock_timestamp(),
						cd_perfil_w,
						cd_cargo_lib_w);
end loop;
close C01;

commit;

end;
$body$
