CREATE OR REPLACE PROCEDURE copiar_man_grupo_planej_estab ( cd_estab_origem_p bigint, cd_estab_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;
cd_empresa_w			smallint;

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.ds_grupo_planej,
	a.ie_situacao
from	man_grupo_planejamento a
where	a.cd_estabelecimento	= cd_estab_origem_p;

vet01	C01%RowType;


BEGIN
select	max(cd_empresa)
into STRICT	cd_empresa_w
from	estabelecimento
where	cd_estabelecimento = cd_estab_destino_p;

open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	nextval('man_grupo_planejamento_seq')
	into STRICT	nr_sequencia_w
	;

	insert into man_grupo_planejamento(
		nr_sequencia,
		ds_grupo_planej,
		cd_estabelecimento,
		dt_atualizacao,
		nm_usuario,
		ie_situacao,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_empresa)
	values ( nr_sequencia_w,
		vet01.ds_grupo_planej,
		cd_estab_destino_p,
		clock_timestamp(),
		nm_usuario_p,
		vet01.ie_situacao,
		clock_timestamp(),
		nm_usuario_p,
		cd_empresa_w);

	insert into man_grupo_planej_estagio(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_grupo_planej,
		nr_seq_estagio)
	SELECT	nextval('man_grupo_planej_estagio_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_w,
		nr_seq_estagio
	from	man_grupo_planej_estagio
	where	nr_seq_grupo_planej = vet01.nr_sequencia;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
