CREATE OR REPLACE PROCEDURE copiar_materiais_estab ( ds_lista_estab_p text, cd_material_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_lista_estab_w	varchar(2000);
cd_estabelecimento_w	smallint;
nr_pos_virgula_w	bigint;

BEGIN
if (ds_lista_estab_p IS NOT NULL AND ds_lista_estab_p::text <> '') and (cd_material_p IS NOT NULL AND cd_material_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	ds_lista_estab_w := ds_lista_estab_p;

	while(ds_lista_estab_w IS NOT NULL AND ds_lista_estab_w::text <> '') loop
		begin
		nr_pos_virgula_w := position(',' in ds_lista_estab_w);

		if (nr_pos_virgula_w > 0) then
			begin
			cd_estabelecimento_w	:= substr(ds_lista_estab_w, 1, position(',' in ds_lista_estab_w) - 1);
			ds_lista_estab_w	:= substr(ds_lista_estab_w, position(',' in ds_lista_estab_w) + 1, length(ds_lista_estab_w));
			end;
		else
			begin
			cd_estabelecimento_w	:= (ds_lista_estab_w)::numeric;
			ds_lista_estab_w	:= null;
			end;
		end if;

		if (coalesce(cd_estabelecimento_w, 0) > 0) then
			begin
			CALL copia_dados_medic_estab(cd_material_p, cd_estabelecimento_p, cd_estabelecimento_w, 1, 'MATERIAL_ESTAB', nm_usuario_p);
			CALL copia_dados_medic_estab(cd_material_p, cd_estabelecimento_p, cd_estabelecimento_w, 1, 'MATERIAL_DILUICAO', nm_usuario_p);
			CALL copia_dados_medic_estab(cd_material_p, cd_estabelecimento_p, cd_estabelecimento_w, 1, 'MATERIAL_PRESCR', nm_usuario_p);
			CALL copia_dados_medic_estab(cd_material_p, cd_estabelecimento_p, cd_estabelecimento_w, 1, 'KIT_MAT_PRESCRICAO', nm_usuario_p);
			CALL copia_dados_medic_estab(cd_material_p, cd_estabelecimento_p, cd_estabelecimento_w, 1, 'MATERIAL_DOSE_TERAP', nm_usuario_p);
			end;
		end if;
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
