CREATE OR REPLACE PROCEDURE copiar_medico_agenda ( cd_agenda_origem_p bigint, cd_agenda_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_medico_w		varchar(10);

c01 CURSOR FOR
	SELECT 	cd_medico
	from   	agenda_medico
	where  	ie_situacao = 'A'
	and     cd_agenda = cd_agenda_origem_p;


BEGIN
if (cd_agenda_origem_p IS NOT NULL AND cd_agenda_origem_p::text <> '') and (cd_agenda_destino_p IS NOT NULL AND cd_agenda_destino_p::text <> '') then

	open c01;
	loop
	fetch 	c01
	into 	cd_medico_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	insert into agenda_medico(
		nr_sequencia,
		cd_agenda,
		cd_medico,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec)
	values (nextval('agenda_medico_seq'),
		cd_agenda_destino_p,
		cd_medico_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p);
	end;
	end loop;
	close c01;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
