CREATE OR REPLACE PROCEDURE copiar_niveis_ccg ( nr_prescricao_p bigint, nr_prescr_orig_p bigint, nr_seq_procedimento_p bigint, nr_seq_proc_novo_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_procedimento_p IS NOT NULL AND nr_seq_procedimento_p::text <> '') then
	begin
	insert into 	prescr_proc_glic(
			nr_sequencia,
			nr_prescricao,
			nr_seq_procedimento,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_protocolo,
			qt_glic_inic,
			qt_glic_fim,
			qt_ui_insulina,
			ds_sugestao,
			qt_glicose,
			qt_ui_insulina_int)
	SELECT		nextval('prescr_proc_glic_seq'),
			nr_prescricao_p,
			nr_seq_proc_novo_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			a.nr_seq_protocolo,
			a.qt_glic_inic,
			a.qt_glic_fim,
			a.qt_ui_insulina,
			a.ds_sugestao,
			a.qt_glicose,
			a.qt_ui_insulina_int
	from		prescr_procedimento b,
			prescr_proc_glic a
	where		b.nr_prescricao = a.nr_prescricao
	and		b.nr_sequencia = a.nr_seq_procedimento
	--and		b.ie_suspenso <> 'S'
	and		b.nr_prescricao = nr_prescr_orig_p
	and		b.nr_sequencia  = nr_seq_procedimento_p;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
