CREATE OR REPLACE PROCEDURE copiar_parametros_usuario ( nm_usuario_origem_p text, nm_usuario_destino_p text, ie_limpa_reg_p text, nm_usuario_p text, ds_observacao_p text, cd_funcao_p bigint default null) AS $body$
DECLARE


cd_funcao_w	bigint;
nr_sequencia_w	bigint;
vl_parametro_w	varchar(255);

c01 CURSOR FOR
	SELECT	a.cd_funcao,
		a.nr_sequencia,
		a.vl_parametro
	from	funcao_param_usuario a
	where	a.nm_usuario_param = nm_usuario_origem_p
	and	a.cd_funcao = coalesce(cd_funcao_p,a.cd_funcao)
	and	not exists (	SELECT	b.cd_funcao
				from	funcao_param_usuario b
				where	b.cd_funcao = a.cd_funcao
				and	b.nr_sequencia = a.nr_sequencia
				and	b.nm_usuario_param = nm_usuario_destino_p)
	order by a.cd_funcao,
		a.nr_sequencia,
		CASE WHEN coalesce(a.cd_estabelecimento,0)=wheb_usuario_pck.get_cd_estabelecimento THEN 1 WHEN coalesce(a.cd_estabelecimento,0)=0 THEN 2  ELSE 3 END;


BEGIN

if (ie_limpa_reg_p	= 'S') then
	if (coalesce(cd_funcao_p::text, '') = '') then
		delete	from funcao_param_usuario
		where	nm_usuario_param = nm_usuario_destino_p;
	else
		delete	from funcao_param_usuario
		where	nm_usuario_param = nm_usuario_destino_p
		and	cd_funcao = cd_funcao_p;
	end if;
end if;

open c01;
loop
fetch c01 into
	cd_funcao_w,
	nr_sequencia_w,
	vl_parametro_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	insert into funcao_param_usuario(
			cd_funcao,
			nr_sequencia,
			nm_usuario_param,
			dt_atualizacao,
			nm_usuario,
			vl_parametro,
			ds_observacao)
		values (cd_funcao_w,
			nr_sequencia_w,
			nm_usuario_destino_p,
			clock_timestamp(),
			nm_usuario_p,
			vl_parametro_w,
			CASE WHEN coalesce(ds_observacao_p::text, '') = '' THEN  'Cópia de parâmetros do usuário '||nm_usuario_origem_p  ELSE substr(ds_observacao_p,1,4000) END );
	exception
	when others then
		null;
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
