CREATE OR REPLACE PROCEDURE copiar_parametro_outro_conv ( nr_sequencia_p bigint, cd_convenio_dest_p text, cd_convenio_orig_p bigint, ie_menos_selec_p text, nm_usuario_p text) AS $body$
DECLARE


cd_convenio_dest_w	integer;

C01 CURSOR FOR
	SELECT	cd_convenio
	from	convenio
	where	cd_convenio <> cd_convenio_orig_p
	and	(((ie_menos_selec_p = '1') and (obter_se_contido(cd_convenio, elimina_aspas(cd_convenio_dest_p)) = 'S'))	or
		((ie_menos_selec_p = '2') and (obter_se_contido(cd_convenio, elimina_aspas(cd_convenio_dest_p)) = 'N')))
	and	ie_situacao = 'A';

BEGIN

open C01;
loop
fetch C01 into
	cd_convenio_dest_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	insert into fatur_etapa_alta(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_convenio,
		ie_tipo_atendimento,
		nr_seq_etapa,
		ie_situacao,
		ie_evento,
		cd_estabelecimento,
		nr_seq_classificacao,
		ie_ultima_conta,
		cd_setor_atendimento,
		cd_perfil,
		pr_prejuizo,
		ie_status,
		nr_seq_motivo_dev,
		nr_seq_estagio,
		nr_seq_etapa_filtro,
		ie_tipo_convenio,
		cd_setor_atend_etapa)
	SELECT	nextval('fatur_etapa_alta_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_convenio_dest_w,
		ie_tipo_atendimento,
		nr_seq_etapa,
		ie_situacao,
		ie_evento,
		cd_estabelecimento,
		nr_seq_classificacao,
		ie_ultima_conta,
		cd_setor_atendimento,
		cd_perfil,
		pr_prejuizo,
		ie_status,
		nr_seq_motivo_dev,
		nr_seq_estagio,
		nr_seq_etapa_filtro,
		ie_tipo_convenio,
		cd_setor_atend_etapa
	from	fatur_etapa_alta
	where	nr_sequencia = nr_sequencia_p;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
